import { createSlice } from "@reduxjs/toolkit"

const MOVIE_SRC = "https://lumiere-a.akamaihd.net/v1/images/p_junglecruise_21740_v2_bb7f0ae4.jpeg"

const initialState = {
    movies: [{movieImage: MOVIE_SRC,
        movieInfo: {
            movieTitle: "Jungle Cruise",
            movieCategory: {},
            movieRating: {},
        }
    }]
}

const CinemaSlicer = createSlice({
    name: "CinemaSlicer",
    initialState,
    reducers: {
        nulling(state, action) {
            state.a = "2"
        }
    }
});

export default CinemaSlicer.reducer
