// Import the configureStore function from @reduxjs/toolkit
import { configureStore } from '@reduxjs/toolkit';
// Import the reducers for Cinema and Header
import CinemaReducer from './path/to/CinemaReducer';
import HeaderReducer from './path/to/HeaderReducer';

// const API_KEY = 4245b374-41f1-4962-ae9f-4e6f3d9044cc

// Configure the Redux store with Cinema and Header reducers
let store = configureStore({
    reducer: {
        Cinema: CinemaReducer, // Reducer for cinema-related state
        Header: HeaderReducer, // Reducer for header-related state
    }
});

// Export the configured store as default
export default store;
