import { createSlice } from "@reduxjs/toolkit"

const MOVIE_SRC = "https://lumiere-a.akamaihd.net/v1/images/p_junglecruise_21740_v2_bb7f0ae4.jpeg"

const API_KEY = "4245b374-41f1-4962-ae9f-4e6f3d9044cc"

const MOVIE_API_SRC = "https://kinopoiskapiunofficial.tech/api/v2.2/films?order=RATING&type=ALL&ratingFrom=0&ratingTo=10&yearFrom=1000&yearTo=3000&page=2"

async function getMovies(url) {
    try {
        const resp = await fetch(url, {
            headers: {
                "Content-Type": "application/json",
                "X-API-KEY": API_KEY,
            }
        });

        const resp_data = await resp.json();
        return resp_data

    } catch (error) {
        console.error("Error fetching and parsing data:", error);
    }
}

let APImovies = await getMovies(MOVIE_API_SRC).then(result => result).items

console.log(APImovies)


const initialState = {
    movies: [
        {
            movieImg: MOVIE_SRC,
            movieInfo: {
                movieTitle: "Jungle Cruise",
                movieCategory: "Huynya",
                movieRating: 9,
            }
        },
        {
            movieImg: MOVIE_SRC,
            movieInfo: {
                movieTitle: "Jungle Cruise",
                movieCategory: "Huynya",
                movieRating: 9,
            }
        }
    ]
};


const CinemaSlicer = createSlice({
    name: "CinemaSlicer",
    initialState,
    reducers: {
        nulling(state, action) {
            state.a = "2"
        }
    }
});

export default CinemaSlicer.reducer
