import React from "react";
import styles from "./Header.module.css"

const Header = (props) => {

    console.log(props.onChangeSearch)
    const onChangeThis = (event) => {
        props.onChangeSearch(event.target.value);
    }

    return (
        <header className={styles["header"]}>
            <div className={styles["header__content"]}>
                <a href="index.html" className={styles["header__logo"]}> CineMAX </a>
                <form>
                    <input type="text" className={styles["header__search"]} onChange={onChangeThis} placeholder="search" value={props.searchText}/>
                </form>
            </div>
        </header>
    )
}

export default Header;
