import React from "react";
import styles from "./Movie.module.css";


const Movie = (props) => {

    const combinedClasses = ["movie__rating", "movie__rating-green"].map(cls => styles[cls]).join(' ');

    if (props.movieRating >= 7) {
        const combinedClasses = ["movie__rating", "movie__rating-green"].map(cls => styles[cls]).join(' ');
    }
    else if (props.movieRating <= 7) {
        const combinedClasses = ["movie__rating", "movie__rating-yellow"].map(cls => styles[cls]).join(' ');
    }

    return (
        <div className={styles["movie"]}>
            <div className={styles["movie__cover-inner"]}>
                <img src={props.movieImg} 
                alt="" className={styles["movie__cover"]} />
                <div className={styles["movie__cover-darkened"]}>
                    
                </div>
            </div>
            <div className={styles["movie__info"]}>
                <div className={styles["movie__title"]}> {props.movieTitle} </div>
                <div className={styles["movie__category"]}> {props.movieCategory} </div>
                <div className={combinedClasses}> {props.movieRating} </div>
            </div>
        </div>
    );
};

export default Movie
