import { createSlice } from "@reduxjs/toolkit";

initialState = {}

const HeaderSlicer = createSlice({
    name: "HeaderSlicer",
    initialState,
    reducers: {
        updateSearchChar(state, action) {
            state.search = action.payload;
        }
    }
});

export default HeaderSlicer.reducer
