import { createSlice } from "@reduxjs/toolkit"

const MOVIE_SRC = "https://lumiere-a.akamaihd.net/v1/images/p_junglecruise_21740_v2_bb7f0ae4.jpeg"

const API_KEY = "4245b374-41f1-4962-ae9f-4e6f3d9044cc"

async function getMovies(url) {
    try {
        const resp = await fetch(url, {
            headers: {
                "accept": "application/json",
                "X-API-KEY": "4245b374-41f1-4962-ae9f-4e6f3d9044cc",
            }
        });

        /* if (!resp.ok) {
            throw new Error(`HTTP error! Status: ${resp.status}`);
        } */

        console.log(resp)

        const resp_data = await resp.json();
        console.log(resp_data); 

    } catch (error) {
        console.error("Error fetching and parsing data:", error);
    }
}

getMovies('https://kinopoiskapiunofficial.tech/api/v2.2/films/12');



const initialState = {
    movies: [
        {
            movieImg: MOVIE_SRC,
            movieInfo: {
                movieTitle: "Jungle Cruise",
                movieCategory: "Huynya",
                movieRating: 9,
            }
        },
        {
            movieImg: MOVIE_SRC,
            movieInfo: {
                movieTitle: "Jungle Cruise",
                movieCategory: "Huynya",
                movieRating: 9,
            }
        }
    ]
};


const CinemaSlicer = createSlice({
    name: "CinemaSlicer",
    initialState,
    reducers: {
        nulling(state, action) {
            state.a = "2"
        }
    }
});

export default CinemaSlicer.reducer
